// getImage.jsx
function importAllImages(context) {
    let images = {};
    context.keys().forEach((key) => {
      const imageId = key.replace(/^\.\/(.*)s\.png$/, '$1'); // Extraer el ID de la imagen desde el nombre del archivo
      images[imageId] = context(key).default;
    });
    return images;
  }
  
  const imageContext = require.context('!!file-loader!../img/product-img', false, /\.(png)$/);
  const imageMap = importAllImages(imageContext);
  
  export function getImageUrl(product) {
    try {
      const image = imageMap[product.imageId];
      return image || ''; // Si no hay una imagen correspondiente, devolver una cadena vacía
    } catch (error) {
      console.error("Error loading image:", error);
      return '';
    }
  }
