import AA01 from '../img/product-img/AA01s.png';
import AA02 from '../img/product-img/AA02s.png';
import AA03 from '../img/product-img/AA03s.png';

const imageMap = {
  AA01: AA01,
  AA02: AA02,
  AA03: AA03,
  // Agrega más importaciones según sea necesario para otros productos
};

export function getImageUrl(product) {
  try {
    const image = imageMap[product.imageId];
    return image || ''; // Si no hay una imagen correspondiente, devolver una cadena vacía
  } catch (error) {
    console.error("Error loading image:", error);
    return '';
  }
}
